export interface ServerTaskInterval {
    /**
     * Employee identifier.
     */
    performerId: string;
    /**
     * Display name of the performer employee.
     */
    performerName: string;
    /**
     * Task description
     */
    description: string;
    /**
     * Does performer is responsible for entry task group.
     */
    isResponsible: boolean;
    /**
     * Task start date
     */
    startDate?: Date;
    /**
     * Task end date.
     */
    endDate?: Date;
    /**
     * Task duration
     */
    duration?: number;
    /**
     * Task order in task group.
     */
    order: number;
}
